const bind = require("./bind")
// @ponicode
describe("bind", () => {
    test("0", () => {
        let callFunction = () => {
            bind("callback detected, not supported yet", "callback detected, not supported yet")
        }
    
        expect(callFunction).not.toThrow()
    })

    test("1", () => {
        let callFunction = () => {
            bind(undefined, undefined)
        }
    
        expect(callFunction).not.toThrow()
    })
})
